import React, { Component } from 'react';
import './navbar.scss';
import { Link } from 'react-router-dom';
import Brand from '../Brand/brand'

class NavBar extends Component {
  constructor() {
    super();
    this.state = {
      copiedEmail: false,
    }
  }

  copyEmail = () => {
    if (!this.state.copiedEmail) {
      const copyBlock = document.getElementById('copy');
      copyBlock.classList.add('show-popup');
      this.setState({copiedEmail: true});

      navigator.clipboard.writeText('bryandhmin@gmail.com');

      setTimeout(() => {
        copyBlock.classList.remove('show-popup');
        this.setState({copiedEmail: false});
      }, 2000);
    }
  }

  render() {
    return (
      <div className="NavBar">
          <div className="copied-popup" id="copy">
            <div className="popup-box">
              Email copied to clipboard!
            </div>
          </div>
        <div className="Bar">
          <Brand />

          <ul id="nav">
            {/* <Link to="/about">
              <li>About</li>
            </Link> */}
            {/* <Link to="/gallery">
              <li>The Gallery</li>
            </Link> */}

            <a href="/https://drive.google.com/file/d/1l3u772aWaQsYtQeuaqJdGw0JdAX3jCjz/view?usp=sharing" className="nav-item">
              <li>Resume</li>
            </a>
            <Link to="/gallery" className="nav-item">
              <li>The Gallery</li>
            </Link>
            <div onClick={this.copyEmail} className="nav-item">
              <li>Contact Me</li>
            </div>

            {/* <span className="nav-item">
              <Link to="/gallery">
                <li>The Gallery</li>
              </Link>
            </span>
            <span className="nav-item">
              <Link to="/gallery">
                <li>Contact</li>
              </Link>
            </span> */}
          </ul>
        </div>
      </div>
    )
  }
}

export default NavBar
