import React, { Component } from 'react';
import './navbar.scss';
import { Link } from 'react-router-dom';
import Brand from '../Brand/brand'

class NavBar extends Component {
  constructor() {
    super();
    this.state = {
      copiedEmail: false,
    }
  }

  copyEmail = () => {
    if (!this.state.copiedEmail) {
      this.state.copiedEmail = true;

      navigator.clipboard.writeText('bryandhmin@gmail.com');
      const bar = document.getElementById('bar');
      const copiedPopup = document.createElement('div');
      copiedPopup.innerHTML = "Copied email to clipboard!";
      copiedPopup.classList.add('copied-popup')
      bar.appendChild(copiedPopup);

      setTimeout(() => {
        bar.removeChild(copiedPopup);
        this.state.copiedEmail = false;
      }, 1000)
    }
  }

  render() {
    return (
      <div className="NavBar">
        <div className="Bar" id="bar">
          <Brand />

          <ul id="nav">
            {/* <Link to="/about">
              <li>About</li>
            </Link> */}
            {/* <Link to="/gallery">
              <li>The Gallery</li>
            </Link> */}


            <Link to="/gallery" className="nav-item">
              <li>The Gallery</li>
            </Link>
            <div onClick={this.copyEmail} className="nav-item">
              <li>Contact Me!</li>
            </div>

            {/* <span className="nav-item">
              <Link to="/gallery">
                <li>The Gallery</li>
              </Link>
            </span>
            <span className="nav-item">
              <Link to="/gallery">
                <li>Contact</li>
              </Link>
            </span> */}
          </ul>
        </div>
      </div>
    )
  }
}

export default NavBar
